mapper.select:

SELECT ASSET_IDX, ASSET_NAME
FROM ASSET
WHERE USER_IDX = #{user_idx}

mapper.update:

UPDATE 
	ASSET
SET
	ASSET_ORDER = #{asset_order}
WHERE
	USER_IDX = #{user_idx}
AND	
	ASSET_IDX = #{asset_idx}

mapper.insert:

INSERT INTO 
	ASSET(USER_IDX
		, ASSET_ORDER
		, ASSET_TYPE
		, CONNECTION_ASSET_IDX
		, ASSET_NAME
		, INITIAL_AMOUNT)
VALUES
	(#{user_idx}
	, #{asset_order}
	, #{asset_type}
	, #{connection_asset_idx}
	, #{asset_name}
	, #{initial_amount})

mapper.update:

UPDATE 
	ASSET
SET 


	
		
		ASSET_NAME = #{asset_name}
		, INITIAL_AMOUNT = #{initial_amount}
		
	
	
		
		ASSET_NAME = #{asset_name}
		, INITIAL_AMOUNT = #{initial_amount}
		
	
	
		
		ASSET_NAME = #{asset_name}
		, CONNECTION_ASSET_IDX = #{connection_asset_idx}
		
	
	
		
		ASSET_NAME = #{asset_name}
		, INITIAL_AMOUNT = #{initial_amount}
		, CONNECTION_ASSET_IDX = #{connection_asset_idx}
		
	


WHERE
	USER_IDX = #{user_idx}
AND
	ASSET_IDX = #{asset_idx}
