mapper.select:
SELECT MAIL_IDX , MAIL_TO , MAIL_SUBJECT , FN_STRIP_HTML_TAGS(MAIL_CONTENT) MAIL_CONTENT , MAIL_STATE , LEFT(REG_DATE,10) REG_DATE FROM MAIL WHERE MAIL_STATE = "SENT"
AND (MAIL_TO LIKE '%${keyword}%' OR MAIL_SUBJECT LIKE '%${keyword}%' OR FN_STRIP_HTML_TAGS(MAIL_CONTENT) LIKE '%${keyword}%')
ORDER BY MAIL_IDX DESC LIMIT #{startPositionOnCurrentPage }, #{numberPostsPerPage }

SELECT MAIL_IDX , MAIL_TO , MAIL_SUBJECT , FN_STRIP_HTML_TAGS(MAIL_CONTENT) MAIL_CONTENT , MAIL_STATE , LEFT(REG_DATE,10) REG_DATE FROM MAIL WHERE MAIL_STATE = "STORED"
AND (MAIL_TO LIKE '%${keyword}%' OR MAIL_SUBJECT LIKE '%${keyword}%' OR FN_STRIP_HTML_TAGS(MAIL_CONTENT) LIKE '%${keyword}%')
ORDER BY MAIL_IDX DESC LIMIT #{startPositionOnCurrentPage }, #{numberPostsPerPage }

